CREATE TABLE demo.profile ( id uuid NOT NULL DEFAULT uuid_generate_v4(), creation_date timestamp NOT NULL, last_action_date timestamp NOT NULL,
ext_id integer, name varchar, age integer, weight integer, phone_number varchar, height integer, boobs_size integer, description varchar, condition varchar, price_in_one integer, price_in_two integer, price_in_night integer, price_out_one integer, price_out_two integer, price_out_night integer, checked boolean, photos varchar, services varchar,
CONSTRAINT profile_pk PRIMARY KEY (id) );
